import React from 'react';
import { View } from 'react-native';
import { withNavigation } from 'react-navigation';
import Carousel from '../components/Carousel';


function ExploreList({ navigation }) {

    function navigateToList( route, items, favorite = "") {
        navigation.navigate(route, {list: items}, favorite);
    }
  
    function navigateToItem( route, item ) {
        if(route == 'GameDetails')
            navigation.navigate(route, {gameId: item});
        else if(route == 'WebViewNews') 
            navigation.navigate(route, {url: item});
    }

    return (
        <View style={global.backgroundColor, {flex:1}}>
            <Carousel name={"Popular Games"} isGame={true} isFavorite={false} showList={navigateToList} showItem={navigateToItem}/> 
            <Carousel name={"My Favorite Games"} isGame={true} isFavorite={true} showList={navigateToList} showItem={navigateToItem}/> 
            <Carousel name={"My Favorite News"} isGame={false} isFavorite={true} showList={navigateToList} showItem={navigateToItem}/> 
        </View>
    );
}

export default withNavigation(ExploreList);
