import { StyleSheet } from 'react-native'

export default StyleSheet.create({
    fontG: {
        fontSize: 22,
        //fontFamily: 'Nunito-Bold.ttf',
        textAlign: 'justify'
    },
    fontP: {
        fontSize: 16,
       // fontFamily: 'Nunito-Regular.ttf',
        textAlign: 'justify'
    },
    fontLight: {
        color: 'black'
    },
    fontDark: {
        color: 'white'
    },
    backgroundDark: {
        backgroundColor: 'black',
        flexGrow: 1,
        padding: 2
    },
    backgroundLight: {
        backgroundColor: 'white',
        flexGrow: 1,
        padding: 2
    },
    container: {
        flex: 1,
        margin: 0,
        backgroundColor: 'black'
    }
})
