import React, { useState, useEffect } from 'react'
import { TouchableOpacity, Text } from 'react-native'
import Icon from 'react-native-vector-icons/FontAwesome';
import api from '../services/api';


export default props => {
    const [currentHeart, setHeart] = useState('heart-o');

    useEffect(() => {
        loadHeart();
    }, []);

    loadHeart = async () => {
        setTimeout(() => {
            if(props.isFavorite){
                setHeart('heart');
            }else{
                setHeart('heart-o');
            }
        }, 100)
    } 

    postFavorite = async (url) => {
        console.log(props.contentId);
        return await api.put(url + props.contentId);
    } 

    function favoriteItem () {
        if(props.isGame){
            postFavorite('games/favorites/');
        }else{
            postFavorite('feeds/favorites/');
        }
        if(currentHeart == 'heart-o'){
            setHeart('heart');
        }else{
            setHeart('heart-o');
        }
    };

    return (
        <>
            <TouchableOpacity style={{ justifyContent: 'center' }} onPress={() => favoriteItem()}>
                <Icon name={props.hearth} size={props.size} color="#17B978" />
            </TouchableOpacity>
        </>
    )
}
