import React, { useState, useEffect } from 'react'
import { SafeAreaView, Text, StyleSheet, Image, TouchableOpacity, FlatList, ActivityIndicator } from 'react-native'
import { withNavigation } from 'react-navigation';
import Style from '../styles/Style'
import api from '../services/api';

function Carousel ( props ) {

    const [popularGames, setPopularGames] = useState([]);
    const [favoriteGames, setFavoriteGames] = useState([]);
    const [favoriteNews, setFavoriteNews] = useState([]);
    const [loading, setLoading] = useState(true);
    
    useEffect(() => {
        loadMyList();
    }, []);

    loadMyList = async () => {
            const response = await api.get('games');
            const response2 = await api.get('games/favorites');
            const response3 = await api.get('feeds/favorites');
        setTimeout(() => {
            setPopularGames([...response.data]);
            setFavoriteGames([...response2.data]);
            setFavoriteNews([...response2.data]);
            setLoading(false);
        }, 100);
    }

  

    return (
        <>
            {loading ? (
                <SafeAreaView style={[styles.centerLoading]}>
                    <ActivityIndicator size="large" color="#494949" />
                </SafeAreaView> 
            ) : (
                <>
                <SafeAreaView style={{flex:1},{width:330}}>
                    <SafeAreaView style={styles.Row}>
                        <Text style={[Style.fontP, global.fontColor]}>Popular Games</Text>
                        <TouchableOpacity onPress={() => props.showList('GameList', myList, '')}>
                            <Text style={[global.fontColor, {fontSize: 13}]}>SEE ALL →</Text>
                        </TouchableOpacity>
                    </SafeAreaView>
                    <FlatList
                    data={myList}
                    keyExtractor={(item,index) => index.toString()}
                    horizontal
                    renderItem={({ item }) => (
                        <TouchableOpacity onPress={() => props.showItem('GameDetails', item.id)}>
                            <Image source={{uri: item.coverUrl}} style={styles.frame}/>
                        </TouchableOpacity>
                    )}/>
                </SafeAreaView>
                <SafeAreaView style={{flex:1},{width:330}}>
                    <SafeAreaView style={styles.Row}>
                        <Text style={[Style.fontP, global.fontColor]}>Favorite Games</Text>
                        <TouchableOpacity onPress={() => props.showList('GameList', myList, '/favorites')}>
                            <Text style={[global.fontColor, {fontSize: 13}]}>SEE ALL →</Text>
                        </TouchableOpacity>
                    </SafeAreaView>
                    <FlatList
                    data={myList}
                    keyExtractor={(item,index) => index.toString()}
                    horizontal
                    renderItem={({ item }) => (
                        <TouchableOpacity onPress={() => props.showItem('GameDetails', item.id)}>
                            <Image source={{uri: item.coverUrl}} style={styles.frame}/>
                        </TouchableOpacity>
                    )}/>
                </SafeAreaView>
                <SafeAreaView style={{flex:1},{width:330}}>
                    <SafeAreaView style={styles.Row}>
                        <Text style={[Style.fontP, global.fontColor]}>Favorite News</Text>
                        <TouchableOpacity onPress={() => props.showList('FavoriteNews', myList)}>
                            <Text style={[global.fontColor, {fontSize: 13}]}>SEE ALL →</Text>
                        </TouchableOpacity>
                    </SafeAreaView>
                    <FlatList
                    data={myList}
                    keyExtractor={(item,index) => index.toString()}
                    horizontal
                    renderItem={({ item }) => (
                        <TouchableOpacity onPress={() => props.showItem('WebViewNews', item.url)}>
                            <Image source={{uri: item.imageUrl}} style={styles.frame}/>
                        </TouchableOpacity>
                    )}/>
                </SafeAreaView>
                </>
            )};
        </>
    );
}

const styles = StyleSheet.create({
    Row: {
        paddingTop: 12,
        paddingBottom: 3,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "flex-end"
    },
    frame: {
        height: 100,
        width: 100,
        borderRadius: 10,
        marginRight: 10
    },
    centerLoading: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    }
})

export default withNavigation(Carousel);
